def today_pokemon(request):
    pokemon_list=[
        {"name":"메타몽",
        "src":"data:image/jpeg;base64,"},
        {"name":"이상해씨",
        "src":"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSrKot4aTLEM7d9Z4tlsq6p3yjdzBD_qDqo2w&usqp=CAU"},
        {"name":"신뇽",
        "src":"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQRdfYloGtt406fuenCsIaj3duPCCdsFv2PjA&usqp=CAU"},
    ]
    context={
        "pokemon":random.choice(pokemon_list)
    }
    return render(request,"today_pokemon.html",context)

def lotto(request):
    lotto_list=[]
    for _ in range(5):
        lotto=random.sample(range(1,46),6)
        lotto_list.append(lotto)

    context={
        "lotto_list":lotto_list,
    }

    return render(request,"lotto.html",context)
    
